<?php/** * Created by PhpStorm. * User: Admin * Date: 04.04.2017 * Time: 8:10 */if ( ! defined( 'POST_VIEWS_PATH' ) ) {	die();}include 'title.php';$date_post = '';if ( array_key_exists( 'post_date', $the_post ) ) {	$date      = new DateTime( trim( $the_post['post_date'] ) );	$date_post = $date->format( $shortcode_mgr->get( 'date_format' ) );}$meta_width = $shortcode_mgr->get( 'meta_width' );if ( $meta_width == "100%" ) {	$width = 'width: 100%;';} else {	$width = "width: " . $meta_width . "px;";}$html .= '<div class="netsposts-source" style="margin-bottom: 5px;' . $width . '">';if ( $shortcode_mgr->get_boolean( 'meta_info' ) ) {	$html .= __( '<span>Published</span>', 'netsposts' ) . '' . __( '<span>', 'netsposts' ) . ' ' . $date_post . '' . __( '</span>', 'netsposts' ) . ' ' . __( '<span>in</span>', 'netsposts' )	         . '  <span><a href="' . $blog_url . '" ' . $open_link_in_new_tab . '>' . $blog_name . '</a></span>';}if($shortcode_mgr->get_boolean('show_order')) {	if ( isset( $tab_order_by1 ) && count( $tab_order_by1 ) > 0 && isset( $the_post[ $tab_order_by1[0] ] ) ) {		$fieldname = $tab_order_by1[0];		$fullname  = netsposts_create_label_from_id($fieldname);		$html      .= "<span>" . $fullname . ": " . $the_post[ $tab_order_by1[0] ] . "</span>";	}}$show_date = false;$date_column = '';if(isset($show_before_date)){    $show_date = true;    $date_column = $show_before_date[0];}if(isset($show_after_date)){    $show_date = true;    $date_column = $show_after_date[0];}if(isset($show_for_today)){    $show_date = true;    $date_column = $show_for_today;}$show_date = $show_date && $shortcode_mgr->get_boolean('show_filter_date') && isset($the_post[$date_column]);if($show_date){    $date = new DateTime($the_post[$date_column]);    $html .= ' <span>' . $date->format("M j") . '</span>';}##  Full metadataif ( $show_author ) {	if ( $column > 1 ) {		$html .= '<br />';	}	$html .= ' <span>' . __( 'Author', 'netsposts' ) . ' ' . '<a href="' . $blog_url . '?author=' . $the_post['post_author'] . '"'	         . $open_link_in_new_tab . '>' . get_the_author_meta( 'display_name', $the_post['post_author'] ) . ' </a></span>';}$html .= '</div>'; //end of netsposts-sourceif( isset( $the_post['categories'] ) ){	$html .= '<div class="netsposts-categories" style="margin-bottom: 5px; ' . $width . '">';	foreach ( $the_post['categories'] as $category ){		$link = $blog_url . '?cat=' . $category['cat_id'];		$html .= '<a href="' . $link . '"'. $open_link_in_new_tab . '>' . $category['cat_name'] . '</a> ';	}	$html .= '</div>';}