<?php/** * Created by PhpStorm. * User: Admin * Date: 04.04.2017 * Time: 8:30 */if ( ! defined( 'POST_VIEWS_PATH' ) ) {	die();}if ( $shortcode_mgr->get_boolean( 'titles_only' ) ) {	$title_class = 'netsposts-post-titles-only';} else {	$title_class = 'netsposts-posttitle';}$temp = sanitize_quotes( $shortcode_mgr->get( 'wrap_title_start' ) );$html .= htmlspecialchars_decode( $temp );/* * This code creates title link */$title_color  = $shortcode_mgr->get( 'title_color' );$title_length = $shortcode_mgr->get( 'title_length' );if ( $shortcode_mgr->get_boolean( 'include_link_title' ) ) {	if ( !isset( $exclude_title_links) || ! array_has_value( $the_post['ID'], $exclude_title_links ) ) {		$html .= '<a href="' . $the_post['guid'] . '"'. $open_link_in_new_tab . '>' . ShortenText( $the_post['post_title'], $title_length ) . '</a>';	} else {		$html .= '<span class="' . $title_class . '" style="color: ' . $title_color . ';">' .		         ShortenText( $the_post['post_title'], $title_length ) . '</span>';	}} else {	$html .= '<span class="' . $title_class . '" style="color: ' . $title_color . ';">' . ShortenText( $the_post['post_title'], $title_length ) . '</span>';}$html .= htmlspecialchars_decode( $shortcode_mgr->get( 'wrap_title_end' ) );