sections.css:
.c-rte a,
.c-rte__link {
  @include default($color: $black);
  transition: all .4s $cubic-transition;
  font-weight: bold;
  border-bottom: 2px solid $light-blue;
  text-decoration: none;

  &:hover,
  &:focus {
    color: $light-blue;
    border-bottom: 2px solid $black;
  }
}

.c-rte h1 {
  font-weight:bold;

  &:before {
    content:" ";
    display: block;
    border-bottom: 5px solid $red;
    width: 20%;
  }
}

.c-rte__text-spacing {
    margin-left: 20%;
}

.c-rte__offset-quote {
    width: 50%;
    float: right;
    padding: 20px;
    background: $light-gray;
    border-bottom-right-radius: 150px;
    padding-right: 15%;

    p {
      font-size: 25px;
      color: $black;

      span {
        float: left;
        display: inline-block;
        width: 100%;
        font-weight: bold;
        text-transform: uppercase;
        font-size: 12px;
        margin-top: 10px;
      }
    }
}

input[type="radio"] {
  display: none;
}

.c-uie__radiobutton {
  display: block;
  position: relative;
  padding-left: 35px;
  margin-bottom: 12px;
  cursor: pointer;
  font-size: 22px;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;

  &:hover {
    input {
      ~ .c-uie__checkmark {
        background-color: #ccc;
      }
    }
  }

  input {
    position: absolute;
    opacity: 0;
    cursor: pointer;

    &:checked {
      ~ .c-uie__checkmark {
        &:after {
          display: block;
          background: $light-blue;
          border: none;
        }
      }
    }
  }

  .c-uie__checkmark {
    border-radius: 50%;

    &:after {
      top: 50%;
      left: 50%;
      width: 70%;
      height: 70%;
      border-radius: 50%;
      transform: translate(-50%, -50%);
    }
  }
}

.c-uie__checkmark {
  position: absolute;
  top: 0;
  left: 0;
  height: 25px;
  width: 25px;
  background-color: #fff;
  border: 1px solid $black;

  &:after {
    content: "";
    position: absolute;
    border: 1px solid $black;
  }
}

.c-uie__checkbox {
  display: block;
  position: relative;
  padding-left: 35px;
  margin-bottom: 12px;
  cursor: pointer;
  font-size: 22px;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;

  &:hover {
    input {
      ~ .c-uie__checkmark {
        background-color: #ccc;
      }
    }
  }

  input {
    position: absolute;
    opacity: 0;
    cursor: pointer;

    &:checked {
      ~ .c-uie__checkmark {
        &:after {
          display: block;
          background: $light-blue;
          border: none;
        }
      }
    }
  }

  .c-uie__checkmark {
    &:after {
      top: 50%;
      left: 50%;
      width: 70%;
      height: 70%;
      transform: translate(-50%, -50%);
    }
  }
}

.c-uie__input {
  input {
    border: none;
    border-radius: 0;
    background: none;
    border-bottom: 2px solid $black;
    padding: 0;
    padding-bottom: 3px;
    box-shadow: none;
    transition: all 0.4s $cubic-transition;
    height: auto;

    &::placeholder {
      color: $black;
    }

    &::-webkit-input-placeholder {
      /* Chrome/Opera/Safari */
      color: $black;
    }

    &::-moz-placeholder {
      /* Firefox 19+ */
      color: $black;
    }

    &:-ms-input-placeholder {
      /* IE 10+ */
      color: $black;
    }

    &:-moz-placeholder {
      /* Firefox 18- */
      color: $black;
    }

    &:hover,
    &:active,
    &:focus {
      box-shadow: none;
      border: none;
      border-bottom: 4px solid $black;
      color: $light-blue;
      padding-bottom: 1px;

      &::placeholder {
        color: $light-blue;
      }

      &::-webkit-input-placeholder {
        /* Chrome/Opera/Safari */
        color: $light-blue;
      }

      &::-moz-placeholder {
        /* Firefox 19+ */
        color: $light-blue;
      }

      &:-ms-input-placeholder {
        /* IE 10+ */
        color: $light-blue;
      }

      &:-moz-placeholder {
        /* Firefox 18- */
        color: $light-blue;
      }
    }
  }
}

.c-uie__calltoaction {
  padding: 10px 20px;
  border-left: 3px solid $red;
  border-right: 3px solid $black;
  position: relative;
  color: $black;
  font-weight: bold;
  text-decoration: none;

  &:before {
    content: " ";
    position: absolute;
    top: 0;
    right: 0;
    display: block;
    border-bottom: 3px solid $black;
    width: 50%;
    transition: all 0.4s $cubic-transition-2;
  }

  &:after {
    content: " ";
    position: absolute;
    bottom: 0;
    left: 0;
    display: block;
    border-bottom: 3px solid $red;
    transition: all 0.4s $cubic-transition-2;
    width: 50%;
  }

  &:hover {
    &:before, &:after {
      transition: all 0.4s $cubic-transition-2;
      width: 100%;
    }
  }
}

.c-uie__calltoaction-secondary {
  padding: 10px 20px;
  position: relative;
  color: $black;
  font-weight: bold;
  text-decoration: none;

  &:before {
    content: " ";
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    border-top: 3px solid $black;
    border-left: 3px solid $black;
    width: 20%;
    height: 50%;
    transition: all 0.4s $cubic-transition-2;
  }

  &:after {
    content: " ";
    position: absolute;
    bottom: 0;
    right: 0;
    display: block;
    border-bottom: 3px solid $light-blue;
    border-right: 3px solid $light-blue;
    transition: all 0.4s $cubic-transition-2;
    width: 20%;
    height: 50%;
  }

  &:hover {
    &:before, &:after {
      transition: all 0.4s $cubic-transition-2;
      width: 100%;
      height: 100%;
    }
  }
}

.c-uie__dropdown {
  float: left;
  width: 100%;
  margin-bottom: 50px;
  position: relative;
  width: 300px;

  &:before {
    content: "";
    position: absolute;
    color: black;
    right: 0;
    bottom: 0;
    width: 25px;
    height: 25px;
    background-image: url("/wp-content/themes/bsdstarter-child/src/img/icons/icon-arrowdown.svg");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    transition: all 0.4s $cubic-transition-2;
  }

  &:hover {
    &:before {
      transform: rotate(180deg);
    }
  }
}

.c-uie__dropdown-heading {
  border-bottom: 1px solid $red;
  color: $red;
  float: left;
  width: 100%;
}

.c-uie__dropdown-options {
  padding: 0;
  float: left;
  width: 100%;
  max-height: 0;
  overflow: hidden;
  transition: all .4s ease-in-out;
  position: absolute;
  opacity: 0;
  background: $white;
  margin-top: 1px;

  &.drop {
    max-height: 10em;
    overflow: auto;
    opacity: 1;
  }
}

.c-uie__dropdown-option {
  list-style: none;
  border-bottom: 1px solid $black;
  position: relative;
  cursor: pointer;
  color: $black;
  font-size: 13px;
  font-weight: 100;
  transition: all 0.4s $cubic-transition-2;

  &:hover {
    color: $light-blue;
    transition: all 0.4s $cubic-transition-2;

    &:before {
      content: "";
      position: absolute;
      border-bottom: 2px solid $black;
      width: 50%;
      bottom: 50%;
      right: 0;
    }
  }
}

.c-ratiometer {
  width: 100%;
  min-width: 700px;
  position: relative;
  min-height: 300px;
}

.c-ratiometer-meter_out {
  position: absolute;
  top: 0;
  left: 0;
  width: 20rem;
  height: 20rem;
  border-radius: 100%;

  // border: 8px solid cornflowerblue;
  padding: 10px;
  background: $light-blue;
  background: -moz-linear-gradient(-45deg, $light-blue 0%, $light-blue 57%, $white 57%, $white 100%);
  background: -webkit-linear-gradient(-45deg, $light-blue 0%, $light-blue 57%, $white 57%, $white 100%);
  background: linear-gradient(135deg, $light-blue 0%, $light-blue 57%, $white 57%, $white 100%);

  // filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='$light-blue', endColorstr='#ffffff',GradientType=1 );
}

.c-rationmeter-text {
  width: 70%;
  position: absolute;
  transform: translateY(70%);
  right: 0;

  h1 {
    margin: 0;
  }
}

.c-rationmeter-meter {
  width: 19rem;
  height: 19rem;
  background: $white;
  border-radius: 100%;
  background-position: center;
  background-size: contain;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  border: 0.5rem solid $white;
}

h1,
h2,
h3,
h4,
h5 {
  margin-bottom: .7em;
  margin-top: 1.5em;
}

// HEADING TEXT 1
h1 {
  @include type-1;
}

// HEADING TEXT 2
h2 {
  @include type-2;
}

// HEADING TEXT 3
h3 {
  @include type-3;
}

// HEADING TEXT 4
h4 {
  @include type-4;
}

// HEADING TEXT 5
h5 {
  @include type-5;
}

// BODY TEXT 

body {
  @include default;
}

p {
  @include default;
  margin-bottom: 15px;
  margin-top: 15px;

  &.small-text {
    @include type-5;
  }
}

strong {
  font-weight: $font-bold;
}

em {
  font-style: italic;
}

