sections.css:
.c-rte a,
.c-rte__link,
.c-rte__accordion a {
  @include default($color: $black);

  transition: all 0.4s $cubic-transition;
  font-weight: $font-normal;
  border-bottom: 4px solid $light-blue;
  text-decoration: none;

  &:hover,
  &:focus {
    color: $light-blue;
    border-bottom: 4px solid $black;
  }
}

.page-title {
  h1 {
    font-weight: bold;
    display: inline-block;
    margin: 0;

    @include type-custom($color: $black, $font-weight: $font-bold, $font-size: 61px, $line-height: 1);

    span {
      font-weight: $font-thin;
    }

    &:before {
      content: " ";
      display: block;
      border-bottom: 10px solid $red;
      width: 75px;
      margin-bottom: 15px;

      @include grid-media($lg) {
        width: 110px;
      }
    }
  }

  .page-title__text-spacing {
    margin-top: 30px;

    &,
    p {
      @include type-custom($color: $dark-gray, $font-weight: $font-thinnest, $font-size: 24px, $line-height: 1.25);
    }
  }
}

blockquote,
.c-rte__offset-quote,
.c-rte__accordion blockquote {
  width: 100%;
  float: right;
  padding: 35px;
  background: $light-gray;
  border-bottom-right-radius: 150px;
  padding-right: 8%;
  margin-left: 30px;
  margin-bottom: 30px;
  max-width: 320px;

  @include grid-media($lg) {
    // width: 40%;
    margin-left: 60px;
    margin-bottom: 60px;
  }


  p {
    @include type-custom($color: $black, $font-weight: $font-thin, $font-size: 38px, $line-height: 1.2);

    // @include grid-media($lg) {
    //   font-size: 25px;
    // }

    em {
      float: left;
      display: inline-block;
      width: 100%;
      font-weight: bold;
      text-transform: uppercase;
      font-size: 12px;
      margin-top: 40px;
      font-style: normal;
    }
  }
}

.c-rte, .c-rte__accordion {
  ul {
    margin: 50px 0px;
    list-style-type: none !important;

    li {
      position: relative;
      padding-left: 40px;

      @include type-custom($color: $black, $font-weight: $font-medium, $font-size: 20px, $line-height: 1.2);

      &:before {
        content: "";
        display: block;
        width: 15px;
        height: 10px;
        background: $maroon;
        position: absolute;
        left: 0;
        top: 5px;
      }

      &:after {
        content: "";
        display: block;
        margin-bottom: 25px;
      }
    }
  }
}

.c-rte, .c-rte__accordion {
  ol {
    margin: 50px 0px;
    list-style-type: none !important;

    li {
      position: relative;
      padding-left: 40px;
      counter-increment: table-ol;

      @include type-custom($color: $black, $font-weight: $font-medium, $font-size: 20px, $line-height: 1.2);

      &:before {
        content: counter(table-ol);
        position: absolute;
        left: 0;
        color: $maroon;
        font-weight: $font-bold;
      }

      &:after {
        content: "";
        display: block;
        margin-bottom: 25px;
      }
    }
  }
}

.c-rte {
  .video {
    border-left: 10px solid $black;
    padding-left: 10px;
    margin-bottom: 50px;

    .caption {
      margin: 0;
      margin-top: 15px;

      // font-style: italic;
      // color: $black;

      // @include type-4;

      // font-weight: $font-normal;
      // line-height: 1;
      @include type-custom($color: $black, $font-weight: $font-bold, $font-size: 14px, $line-height: 1);
    }
  }
}

// Image
.c-rte, .c-rte__accordion {
  img {
    width: 100%;
    height: 100%;
    border-left: 10px solid #050505;
    padding-left: 10px;
  }
}

.c-rte {
  p {
    margin-top: 0;
    margin-bottom: 40px;
  }

  strong {
    @include type-custom($color: $black, $font-weight: $font-bold, $font-size: 19px, $line-height: 1.2);
  }
}

input {
  outline: none;
}

input[type="radio"] {
  display: none;
}

.c-uie__radiobutton {
  display: block;
  position: relative;
  padding-left: 35px;
  margin-bottom: 12px;
  cursor: pointer;
  font-size: 22px;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;

  &:hover {
    input {
      ~ .c-uie__checkmark {
        background-color: #ccc;
      }
    }
  }

  input {
    position: absolute;
    opacity: 0;
    cursor: pointer;

    &:checked {
      ~ .c-uie__checkmark {
        &:after {
          display: block;
          background: $light-blue;
          border: none;
        }
      }
    }
  }

  .c-uie__checkmark {
    border-radius: 50%;

    &:after {
      top: 50%;
      left: 50%;
      width: 70%;
      height: 70%;
      border-radius: 50%;
      transform: translate(-50%, -50%);
    }
  }
}

.c-uie__checkmark {
  position: absolute;
  top: 0;
  left: 0;
  height: 25px;
  width: 25px;
  background-color: #fff;
  border: 1px solid $black;

  &:after {
    content: "";
    position: absolute;
    border: 1px solid $black;
  }
}

.c-uie__checkbox {
  display: block;
  position: relative;
  padding-left: 35px;
  margin-bottom: 12px;
  cursor: pointer;
  font-size: 22px;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;

  &:hover {
    input {
      ~ .c-uie__checkmark {
        background-color: #ccc;
      }
    }
  }

  input {
    position: absolute;
    opacity: 0;
    cursor: pointer;

    &:checked {
      ~ .c-uie__checkmark {
        &:after {
          display: block;
          background: $light-blue;
          border: none;
        }
      }
    }
  }

  .c-uie__checkmark {
    &:after {
      top: 50%;
      left: 50%;
      width: 70%;
      height: 70%;
      transform: translate(-50%, -50%);
    }
  }
}

.c-uie__input {
  input {
    border: none;
    border-radius: 0;
    background: none;
    border-bottom: 2px solid $black;
    padding: 0;
    padding-bottom: 3px;
    box-shadow: none;
    transition: all 0.4s $cubic-transition;
    height: auto;

    &::placeholder {
      color: $black;
    }

    &::-webkit-input-placeholder {
      /* Chrome/Opera/Safari */
      color: $black;
    }

    &::-moz-placeholder {
      /* Firefox 19+ */
      color: $black;
    }

    &:-ms-input-placeholder {
      /* IE 10+ */
      color: $black;
    }

    &:-moz-placeholder {
      /* Firefox 18- */
      color: $black;
    }

    &:hover,
    &:active,
    &:focus {
      box-shadow: none;
      border: none;
      border-bottom: 4px solid $black;
      color: $light-blue;
      padding-bottom: 1px;

      &::placeholder {
        color: $light-blue;
      }

      &::-webkit-input-placeholder {
        /* Chrome/Opera/Safari */
        color: $light-blue;
      }

      &::-moz-placeholder {
        /* Firefox 19+ */
        color: $light-blue;
      }

      &:-ms-input-placeholder {
        /* IE 10+ */
        color: $light-blue;
      }

      &:-moz-placeholder {
        /* Firefox 18- */
        color: $light-blue;
      }
    }
  }
}

.c-uie__calltoaction {
  padding: 15px 24px;
  border-left: 4px solid $red;
  border-right: 4px solid $black;
  position: relative;
  text-decoration: none;

  @include type-custom($color: $black, $font-weight: $font-bold, $font-size: 22px, $line-height: 1);

  @include grid-media($md) {
    @include type-custom($color: $black, $font-weight: $font-bold, $font-size: 26px, $line-height: 1);
  }


  &:before {
    content: " ";
    position: absolute;
    top: 0;
    right: 0;
    display: block;
    border-bottom: 4px solid $black;
    width: 50%;
    transition: all 0.4s $cubic-transition-2;
  }

  &:after {
    content: " ";
    position: absolute;
    bottom: 0;
    left: 0;
    display: block;
    border-bottom: 4px solid $red;
    transition: all 0.4s $cubic-transition-2;
    width: 50%;
  }

  &:hover {
    &:before, &:after {
      transition: all 0.4s $cubic-transition-2;
      width: 100%;
    }
  }
}

.c-uie__calltoaction-secondary {
  padding: 10px 20px;
  position: relative;
  color: $black;
  font-weight: bold;
  text-decoration: none;

  &:before {
    content: " ";
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    border-top: 4px solid $black;
    border-left: 4px solid $black;
    width: 50%;
    height: 50%;
    transition: all 0.4s $cubic-transition-2;
  }

  &:after {
    content: " ";
    position: absolute;
    bottom: 0;
    right: 0;
    display: block;
    border-bottom: 4px solid $red;
    border-right: 4px solid $red;
    transition: all 0.4s $cubic-transition-2;
    width: 50%;
    height: 50%;
  }

  &:hover {
    &:before, &:after {
      transition: all 0.4s $cubic-transition-2;
      width: calc(100% - 4px);
      height: calc(100% - 4px);
    }
  }
}

.c-uie__dropdown {
  float: left;
  width: 100%;
  margin-top: 50px;
  position: relative;
  width: 300px;
}

.c-uie__dropdown-heading {
  border-bottom: 1px solid $red;
  color: $red;
}

.c-uie__dropdown-options {
  padding: 0;
  float: left;
  width: 100%;
  max-height: 0;
  overflow: hidden;
  transition: all .4s ease-in-out;
  position: absolute;
  opacity: 0;
  background: $white;
  margin-top: 1px;

  &.drop {
    max-height: 10em;
    overflow: auto;
    opacity: 1;
  }
}

.c-uie__dropdown-option {
  list-style: none;
  border-bottom: 1px solid $black;
  position: relative;
  cursor: pointer;
  color: $black;
  font-size: 13px;
  font-weight: 100;
  transition: all 0.4s $cubic-transition-2;

  &:hover {
    color: $light-blue;
    transition: all 0.4s $cubic-transition-2;

    &:before {
      content: "";
      position: absolute;
      border-bottom: 2px solid $black;
      width: 50%;
      bottom: 50%;
      right: 0;
    }
  }
}

.c-image {
  width: 100%;
  max-width: 350px;
}

.c-image__image {
  float: left;
  margin-bottom: 10px;
}

.c-image__caption {
  position: relative;
  float: left;
  clear: both;

  @include type-custom($color: $black, $font-weight: $font-bold, $font-size: 14px, $line-height: 1.3);

  text-align: left;
  margin-bottom: 10px;
  padding-bottom: 15px;

  &:after {
    content: '';
    position: absolute;
    left: 0;
    bottom: 0;
    width: 120px;
    height: 2px;
    background: $light-gray;
  }
}

.hero_image {
  padding: 0;
}

.c-hero_image__wrapper {
  position: relative;

  @include grid-media($md) {
    height: 271px;
  }


  @include grid-media($lg) {
    height: 369px;
  }


  .c-hero_image {
    @include grid-column(12, $no-gutter);

    float: none;
    height: 164px;
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    padding: 0;

    @include grid-media($md) {
      height: 271px;
    }


    @include grid-media($lg) {
      height: 369px;
    }
  }

  .c-hero_image--large_text, .c-hero_image--small_text {
    // margin-bottom: 45%;

    @include grid-media($sm) {
      // margin-bottom: 30%;
    }


    @include grid-media($md) {
      // margin-bottom: 0;
    }


    @include grid-media($lg) {
      @include grid-column(8, $no-gutter);

      display: inline-block;
      float: right;
      margin-left: -10%;
    }
  }

  h1 {
    font-size: 30px;
    background: white;
    padding: 15px 20px;
    width: 60%;
    margin: 0;
    margin-top: -50px;
    position: relative;

    @include grid-media($lg) {
      @include type-custom($color: $black, $font-weight: $font-thinnest, $font-size: 120px, $line-height: 0.8);

      max-width: 600px;
      background: none;
      float: left;
      margin: 0;
      margin-left: 65px;
      display: inline-block;
      position: absolute;
      z-index: 10;
    }
  }

  .c-hero_image--large_text + h1 {
    @include grid-media($lg) {
      position: relative;
      margin-top: -370px;
    }
  }

  h2 {
    font-size: 30px;
    background: white;
    padding: 15px 20px;
    width: 60%;
    margin: 0;
    margin-top: -50px;
    position: relative;

    @include grid-media($lg) {
      @include type-custom($color: $black, $font-weight: $font-thinnest, $font-size: 90px, $line-height: 0.8);

      // @include grid-column(6, $no-gutter);
      width: 515px;
      background: none;
      float: left;
      margin: 0;
      margin-left: 65px;
      display: inline-block;
      position: absolute;
      z-index: 10;
      top: 50%;
      transform: translateY(-50%);
    }
  }
}

.c-signup {
  max-width: 350px;
  padding-bottom: 20px;
}

.c-signup__title {
  text-align: left;
  font-weight: $font-thinnest;
  margin-top: 0;
}

.c-signup,
.c-signup__form {
  @include grid-column(12, $no-gutter);
}

.c-signup__input {
  float: left;
  background: none;
  border: none;
  border-bottom: 3px solid $black;
  padding: 10px 0;
  width: calc(100% - 30px);

  @include type-custom($color: $black, $font-weight: $font-normal, $font-size: 24px, $line-height: 1.3);

  &::placeholder {
    color: $dark-gray;
  }
}

.c-signup__button {
  float: left;
  background: url("/wp-content/themes/seo-main/assets/img/icons/icon-arrow-submit.svg") transparent left -1px center no-repeat;
  background-size: 22px 20px;
  border: 3px solid $red;
  border-radius: 100%;
  transition: all 0.3s $cubic-transition-2;
  width: 30px;
  height: 30px;
  margin-top: 38px;

  &:hover,
  &:focus {
    background: url("/wp-content/themes/seo-main/assets/img/icons/icon-arrow-submit--hover.svg") $red left -1px center no-repeat;
    background-size: 22px 20px;
    border: 3px solid red;
  }
}

button.c-uie__calltoaction-secondary {
  background: none;
  border: none;
  padding: 10px 20px;
  position: relative;
  color: #050505;
  font-weight: 700;
  text-decoration: none;
  font-family: inherit;
  font-size: inherit;
  margin-top: 40px;
  cursor: pointer;
}

.c-accordion__wrapper {
  @include grid-container;

  margin: 20px 0;

  h3 {
    @include type-5;

    // font-weight: bold;
    color: $brown;
    text-transform: uppercase;
    margin: 0;
  }
}

.c-accordion {
  list-style: none;
  padding: 0;
  margin: 0;

  .answer {
    overflow: hidden;
    display: none;
    width: 80%;
    padding: 0 15px;

    p {
      margin-top: 0;
    }

    img {
      margin: 35px 0;
    }
  }

  .c-rte__accordion {
    font-size: initial;
    color: initial;
  }

  li {
    margin: 0;

    // &:hover {
    //   background: $red;

    //   .toggle {
    //     color: $white;
    //   }
    // }

    .toggle {
      @include type-6;

      line-height: 1 !important;
      width: 100%;
      display: block;
      font-weight: $font-bold;

      // background: $white;
      transition: all 0.3s $cubic-transition;
      border-bottom: 1px solid #dbd7cc;

      // padding-bottom: 15px;
      position: relative;

      @include grid-media($lg) {
        &:hover {
          span {
            background: $red;
            color: $white;

            &:after {
              background: url("/wp-content/themes/seo-main/assets/img/icons/carat-accordion-open.svg") right center no-repeat;
            }
          }

          &.active {
            span {
              &:after {
                transform: rotate(0deg);
              }
            }
          }
        }
      }


      &.active {
        span {
          &:after {
            transform: rotate(90deg);
          }
        }
      }

      span {
        max-width: 100%;
        width: 100%;
        display: block;
        padding-top: 15px;
        padding-bottom: 15px;
        padding: 25px 15px;
        padding-right: 95px;

        &:after {
          content: "";
          position: absolute;
          width: 30px;
          height: 30px;
          background: url("/wp-content/themes/seo-main/assets/img/icons/carat-accordion-close.svg") right center no-repeat;
          right: 15px;
          top: 17px;
          transition: all 0.3s $cubic-transition;
        }
      }

      span.content-grid {
        cursor: pointer;

        h1 {
          margin: 0;
          font-size: inherit;
          width: 80%;
        }
      }
    }
  }
}

.c-accordion__filter {
  display: none;

  @include grid-media($md) {
    display: block;
    margin-bottom: 40px;
  }


  li {
    display: inline;
    text-transform: uppercase;
    font-size: 14px;
    color: $black;
    margin-right: 30px;
    cursor: pointer;

    &:hover {
      color: $maroon;
    }

    &.active-selection {
      font-weight: 100;
      color: $maroon;
    }
  }
}

.c-accordion__filter-mobile {
  border-top: 1px solid $light-gray;
  border-bottom: 1px solid $light-gray;
  padding: 10px 0px;

  @include grid-media($md) {
    display: none;
  }


  h3 {
    display: inline-block;
    margin-right: 10px;
    text-transform: lowercase;

    @include type-custom($color: $black, $font-weight: $font-bold, $font-size: 14px, $line-height: 1);
  }

  #select {
    text-transform: uppercase;
    cursor: pointer;

    @include type-custom($color: $maroon, $font-weight: $font-bold, $font-size: 14px, $line-height: 1);
  }

  #sel-option {
    margin-left: 62px;
    display: block;

    li {
      font-size: 14px;
      display: inline-block;
      width: 100%;
      display: none;
      margin-bottom: 5px;
    }
  }
}

.container {
  &.faq {
    padding: 0;

    @include grid-media($sm) {
      @include grid-column(8, $no-gutter);

      padding: inherit;
      float: right;
      padding-left: 25px;
    }
  }
}

.video-comp {
  background: $black;
  margin: 0;

  .video-component {
    padding: 40px 30px;

    @include grid-media($lg) {
      height: 548px;
      padding: 40px 60px;

      .video {
        .plyr__video-embed, .plyr {
          position: static;
        }

        .plyr__video-embed {
          padding: 0px;
        }
      }
    }


    .heading {
      @include grid-column(12, $no-gutter);

      @include grid-media($lg) {
        @include grid-column(2, $no-gutter);

        height: 460px;
        margin-right: 60px;
      }


      position: relative;

      h1 {
        color: $white;
        width: 100%;
        top: 0;
        margin: 0;
        margin-bottom: 25px;

        @include type-custom($color: $white, $font-weight: $font-bold, $font-size: 32px, $line-height: 1);

        @include grid-media($lg) {
          position: absolute;
          margin-bottom: 0;
        }
      }
    }

    .video {
      @include grid-column(12, $no-gutter);

      padding-top: 20px;

      @include grid-media($lg) {
        @include grid-column(6, $no-gutter);

        padding-top: 0px;

        // padding-left: 20px;
        height: 460px;

        .plyr {
          max-height: 460px;
        }
      }


      position: relative;

      &:hover {
        .plyr--playing {
          .plyr__play-large {
            opacity: 1;
            visibility: visible;
          }
        }
      }

      iframe {
        width: 100%;
        height: 100%;
        margin-bottom: 25px;

        @include grid-media($lg) {
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);

          // padding: 20px;
          margin-bottom: 0;
        }
      }
    }

    .quote {
      @include grid-column(12, $no-gutter);

      padding-top: 20px;

      @include grid-media($lg) {
        @include grid-column(2, $no-gutter);

        padding-top: 0px;
        padding-left: 80px;
        height: 460px;
      }


      position: relative;

      p {
        @include type-custom($color: $white, $font-weight: $font-bold, $font-size: 14px, $line-height: 1.3);

        width: 100%;
        bottom: 0;
        margin: 0;
        padding-left: 30%;

        @include grid-media($lg) {
          position: absolute;
          padding-left: 0;
        }


        &:after {
          content: "";
          display: block;

          @include grid-column(10, $no-gutter);

          float: none;
          height: 2px;
          background: white;
          margin-top: 20px;
        }
      }
    }
  }
}

.video {
  width: 100%;
  height: 100%;
  position: relative;
}

iframe {
  width: 100%;
}

h1,
h2,
h3,
h4,
h5 {
  margin-bottom: .7em;
  margin-top: 1.5em;
}

// HEADING TEXT 1
h1 {
  @include type-1;
}

// HEADING TEXT 2
h2 {
  @include type-2;
}

// HEADING TEXT 3
h3 {
  @include type-3;
}

// HEADING TEXT 4
h4 {
  @include type-4;
}

// HEADING TEXT 5
h5 {
  @include type-5;
}

// BODY TEXT 

body {
  @include default;
}

p {
  @include default;
  margin-bottom: 15px;
  margin-top: 15px;

  &.small-text {
    @include type-5;
  }
}

strong {
