// POPUP
.po__popup {
  background: $white;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
  display: flex;
  flex-wrap: wrap;

  // transition: all 0.4s $cubic-transition;
  // transform: scale(0);
  // opacity: 0;

  @include grid-media($lg) {
    @include grid-collapse;
  }


  .po__content {
    padding: 30px;
    height: 75%;

    @include grid-media($md) {
      height: 65%;
    }


    @include grid-media($lg) {
      display: flex;
      align-items: center;
      height: 100%;
      padding: 0 30px;

      .po__content-wrapper {
        max-width: 550px;
      }

      @include grid-column(7);

      @include grid-shift(-5);
    }


    a {
      background-image: linear-gradient(to right, rgba(210, 42, 47, 1) 0%, rgba(255, 103, 27, 1) 100%);
      background-repeat: repeat-x;
      background-position: 0 100%;
      background-size: 100% 2px;
      padding-bottom: 5px;
    }
  }

  .po__content-title {
    margin: 0;
  }

  &.popup-active {
