.c-rte, .c-rte__accordion {
  ul {
    margin: 50px 0px;
    list-style-type: none !important;

    li {
      position: relative;
      padding-left: 40px;

      @include type-custom($color: $black, $font-weight: $font-medium, $font-size: 20px, $line-height: 1.2);

      &:before {
        content: "";
        display: block;
        width: 15px;
        height: 10px;
        background: $maroon;
        position: absolute;
        left: 0;
        top: 5px;
      }

      &:after {
        content: "";
        display: block;
        margin-bottom: 25px;
      }
    }
  }
}

.c-rte, .c-rte__accordion {
  ol {
    margin: 50px 0px;
    list-style-type: none !important;

    li {
      position: relative;
      padding-left: 40px;
      counter-increment: table-ol;

      @include type-custom($color: $black, $font-weight: $font-medium, $font-size: 20px, $line-height: 1.2);

      &:before {
        content: counter(table-ol);
        position: absolute;
        left: 0;
        color: $maroon;
        font-weight: $font-bold;
      }

      &:after {
        content: "";
        display: block;
        margin-bottom: 25px;
      }
    }
  }
}

.c-rte {
