    margin-bottom: 50px;

    .caption {
      margin: 0;
      margin-top: 15px;

      // font-style: italic;
      // color: $black;

      // @include type-4;

      // font-weight: $font-normal;
      // line-height: 1;
      @include type-custom($color: $black, $font-weight: $font-bold, $font-size: 14px, $line-height: 1);
    }
  }
}

// Image
.c-rte, .c-rte__accordion {
