      display: inline-block;
      position: absolute;
      z-index: 10;
      top: 50%;
      transform: translateY(-50%);
    }
  }
}

.c-signup {
  max-width: 350px;
  padding-bottom: 20px;
}

.c-signup__title {
  text-align: left;
  font-weight: $font-thinnest;
  margin-top: 0;
}

.c-signup,
.c-signup__form {
  @include grid-column(12, $no-gutter);
}

.c-signup__input {
  float: left;
  background: none;
  border: none;
  border-bottom: 3px solid $black;
  padding: 10px 0;
  width: calc(100% - 30px);

  @include type-custom($color: $black, $font-weight: $font-normal, $font-size: 24px, $line-height: 1.3);

  &::placeholder {
    color: $dark-gray;
  }
}

.c-signup__button {
  float: left;
  background: url("/wp-content/themes/seo-main/assets/img/icons/icon-arrow-submit.svg") transparent left -1px center no-repeat;
  background-size: 22px 20px;
  border: 3px solid $red;
  border-radius: 100%;
  transition: all 0.3s $cubic-transition-2;
  width: 30px;
  height: 30px;
  margin-top: 38px;

  &:hover,
  &:focus {
    background: url("/wp-content/themes/seo-main/assets/img/icons/icon-arrow-submit--hover.svg") $red left -1px center no-repeat;
    background-size: 22px 20px;
    border: 3px solid red;
  }
}

button.c-uie__calltoaction-secondary {
  background: none;
  border: none;
  padding: 10px 20px;
  position: relative;
  color: #050505;
  font-weight: 700;
  text-decoration: none;
  font-family: inherit;
  font-size: inherit;
  margin-top: 40px;
  cursor: pointer;
}

.c-accordion__wrapper {
  @include grid-container;

  margin: 20px 0;

  h3 {
    @include type-5;

    // font-weight: bold;
    color: $brown;
    text-transform: uppercase;
    margin: 0;
  }
}

.c-accordion {
  list-style: none;
  padding: 0;
  margin: 0;

  .answer {
    overflow: hidden;
    display: none;
    width: 80%;
    padding: 0 15px;

    p {
      margin-top: 0;
    }

    img {
      margin: 35px 0;
    }
  }

  .c-rte__accordion {
    font-size: initial;
    color: initial;
  }

  li {
    margin: 0;

    // &:hover {
    //   background: $red;

    //   .toggle {
    //     color: $white;
    //   }
    // }

    .toggle {
      @include type-6;

      line-height: 1 !important;
      width: 100%;
      display: block;
      font-weight: $font-bold;

      // background: $white;
      transition: all 0.3s $cubic-transition;
      border-bottom: 1px solid #dbd7cc;

      // padding-bottom: 15px;
      position: relative;

      @include grid-media($lg) {
        &:hover {
          span {
            background: $red;
            color: $white;

            &:after {
              background: url("/wp-content/themes/seo-main/assets/img/icons/carat-accordion-open.svg") right center no-repeat;
            }
          }

          &.active {
            span {
              &:after {
                transform: rotate(0deg);
              }
            }
          }
        }
      }


      &.active {
        span {
          &:after {
            transform: rotate(90deg);
          }
        }
      }

      span {
        max-width: 100%;
        width: 100%;
        display: block;
        padding-top: 15px;
        padding-bottom: 15px;
        padding: 25px 15px;
        padding-right: 95px;

        &:after {
          content: "";
          position: absolute;
          width: 30px;
          height: 30px;
          background: url("/wp-content/themes/seo-main/assets/img/icons/carat-accordion-close.svg") right center no-repeat;
          right: 15px;
          top: 17px;
          transition: all 0.3s $cubic-transition;
        }
      }

      span.content-grid {
        cursor: pointer;

        h1 {
          margin: 0;
          font-size: inherit;
          width: 80%;
        }
      }
    }
  }
}

.c-accordion__filter {
  display: none;

  @include grid-media($md) {
    display: block;
    margin-bottom: 40px;
  }


  li {
    display: inline;
    text-transform: uppercase;
    font-size: 14px;
    color: $black;
    margin-right: 30px;
    cursor: pointer;

    &:hover {
      color: $maroon;
    }

    &.active-selection {
      font-weight: 100;
      color: $maroon;
    }
  }
}

.c-accordion__filter-mobile {
  border-top: 1px solid $light-gray;
  border-bottom: 1px solid $light-gray;
  padding: 10px 0px;

  @include grid-media($md) {
    display: none;
  }


  h3 {
    display: inline-block;
    margin-right: 10px;
    text-transform: lowercase;

    @include type-custom($color: $black, $font-weight: $font-bold, $font-size: 14px, $line-height: 1);
  }

  #select {
    text-transform: uppercase;
    cursor: pointer;

    @include type-custom($color: $maroon, $font-weight: $font-bold, $font-size: 14px, $line-height: 1);
  }

  #sel-option {
    margin-left: 62px;
    display: block;

    li {
      font-size: 14px;
      display: inline-block;
      width: 100%;
      display: none;
      margin-bottom: 5px;
    }
  }
}

.container {
  &.faq {
    padding: 0;

    @include grid-media($sm) {
      @include grid-column(8, $no-gutter);

      padding: inherit;
      float: right;
      padding-left: 25px;
    }
  }
}

.video-comp {
  background: $black;
  margin: 0;

  .video-component {
    padding: 40px 30px;

    @include grid-media($lg) {
      height: 548px;
      padding: 40px 60px;

      .video {
        .plyr__video-embed, .plyr {
          position: static;
        }

        .plyr__video-embed {
          padding: 0px;
        }
      }
    }


    .heading {
      @include grid-column(12, $no-gutter);

      @include grid-media($lg) {
        @include grid-column(2, $no-gutter);

        height: 460px;
        margin-right: 60px;
      }


      position: relative;

      h1 {
        color: $white;
        width: 100%;
        top: 0;
        margin: 0;
        margin-bottom: 25px;

        @include type-custom($color: $white, $font-weight: $font-bold, $font-size: 32px, $line-height: 1);

        @include grid-media($lg) {
          position: absolute;
          margin-bottom: 0;
        }
      }
    }

    .video {
      @include grid-column(12, $no-gutter);

      padding-top: 20px;

      @include grid-media($lg) {
        @include grid-column(6, $no-gutter);

        padding-top: 0px;

        // padding-left: 20px;
        height: 460px;

        .plyr {
          max-height: 460px;
        }
      }


      position: relative;

      &:hover {
        .plyr--playing {
          .plyr__play-large {
            opacity: 1;
            visibility: visible;
          }
        }
      }

      iframe {
        width: 100%;
        height: 100%;
        margin-bottom: 25px;

        @include grid-media($lg) {
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);

          // padding: 20px;
          margin-bottom: 0;
        }
      }
    }

    .quote {
      @include grid-column(12, $no-gutter);

      padding-top: 20px;

      @include grid-media($lg) {
        @include grid-column(2, $no-gutter);

        padding-top: 0px;
        padding-left: 80px;
        height: 460px;
      }


      position: relative;

      p {
        @include type-custom($color: $white, $font-weight: $font-bold, $font-size: 14px, $line-height: 1.3);

        width: 100%;
        bottom: 0;
        margin: 0;
        padding-left: 30%;

        @include grid-media($lg) {
          position: absolute;
          padding-left: 0;
        }


        &:after {
          content: "";
          display: block;

          @include grid-column(10, $no-gutter);

          float: none;
          height: 2px;
          background: white;
          margin-top: 20px;
        }
      }
    }
  }
}

.video {
  width: 100%;
  height: 100%;
  position: relative;
}

iframe {
  width: 100%;
}

h1,
h2,
h3,
h4,
h5 {
  margin-bottom: .7em;
  margin-top: 1.5em;
}

// HEADING TEXT 1
h1 {
  @include type-1;
}

// HEADING TEXT 2
h2 {
  @include type-2;
}

// HEADING TEXT 3
h3 {
  @include type-3;
}

// HEADING TEXT 4
h4 {
  @include type-4;
}

// HEADING TEXT 5
h5 {
  @include type-5;
}

// BODY TEXT 

body {
  @include default;
}

p {
  @include default;
  margin-bottom: 15px;
  margin-top: 15px;

  &.small-text {
    @include type-5;
  }
}

strong {
  font-weight: $font-bold;
}

em {
  font-style: italic;
}

a {
  text-decoration: none;
}

.c-gallery {
  background: $white;
  margin-bottom: 40px;
}

.gd__content {
  background: $white;
  padding: 30px;
}

.gd__content-title {
  @include default;

  font-weight: $font-normal;
  margin: 0 0 15px;
}

.gd__content-link {
  color: $black;

  @include type-5-bold;

  display: flex;
  align-items: center;
}

/* -----------------
Gallery
-------------------- */
.c-gallery__slide {
  // position: fixed;
  top: 0;
  left: 0;
  background: $white;

  * {
    color: $black;
  }

  &:before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;

    // background: rgba(20, 20, 19, 0.65);
 
  }
}

.c-gallery__slide {
  // padding: 50px;
}

.c-gallery__slide,
#slider-container {
  width: 100%;
  background: $white;

  .po__close {
    background: none;
    width: 24px;
    height: 24px;
  }
}

// #slider-container {
//   height: 315px;
// }

.slideshow-cont {
  width: 100% !important;

  // height: 100% !important;
  position: static !important;
}

.bx-viewport {
  border: none !important;
  left: 0 !important;
  box-shadow: none !important;
  overflow: unset !important;

  // height: 100% !important;
}

.bx-wrapper,
.container-slides {
  // height: 100%;
  position: relative;
  z-index: 0;
}

// COLUMN SETUP
.gal-row {
  display: block;
  height: 50%;
  position: relative;

  @include grid-media($md) {
    height: 100%;

    @include grid-column(7, $no-gutter);

    &:last-child {
      @include grid-column(5, $no-gutter);
    }
  }


  @include grid-media($lg) {
    @include grid-column(8, $no-gutter);

    &:last-child {
      @include grid-column(4, $no-gutter);
    }
  }
}

// GROUPING
.group:before,
.group:after {
  content: "";
  display: table;
}

.group:after {
  clear: both;
}

.group {
  zoom: 1;

  /* For IE 6/7 */
}

.body-slide {
  padding: 30px;
  overflow-y: auto;
  max-height: 45%;
  max-height: calc(100% - 180px);

  @include grid-media($md) {
    max-height: 72.5%;
  }


  p {
    @include type-5;

    &:first-child {
      margin-top: 0;
    }

    &:last-child {
      margin-bottom: 0;
    }
  }
}

.bs-title {
  margin: 0;
  display: block;
  padding-top: 25px;

  @include type-custom($color: $black, $font-weight: $font-bold, $font-size: 14px, $line-height: 1);

  // border-bottom: 1px solid $white;

  @include grid-column(5.2, $no-gutter);

  @include grid-media($md) {
    padding-top: 30px;

    @include grid-column(8, $no-gutter);
  }


  @include grid-media($lg) {
    padding-top: 10px;
  }
}

.controls {
  // border-top: 1px solid $white;

  @include grid-column(6.8, $no-gutter);

  color: $white;
  background: $white;
  padding: 10px 10px;
  position: absolute;
  right: 0;
  top: 10px;
  bottom: 0;
  display: flex;
  align-items: center;
  text-align: right;
  padding-right: 0;
  padding-bottom: 0;

  @include grid-media($md) {
    @include grid-column(4, $no-gutter);

    padding: 10px 10px;
    padding-bottom: 0;
    padding-right: 0;
  }


  @include grid-media($lg) {
    padding: 10px 30px;
    padding-right: 0;
    padding-top: 0;
    padding-bottom: 0;
  }


  .pagination {
    @include grid-column(12, $no-gutter);

    span {
      position: relative;
      top: -15px;

      @include type-custom($color: $black, $font-weight: $font-thinnest, $font-size: 14px, $line-height: 1);

      font-style: italic;
    }
  }

  .arrows {
    // @include grid-column(3, $no-gutter);

    width: 50px;
    margin-left: 10px;
    float: right;

    // margin-top: 10px;

    // padding-right: 10px;

    @include grid-media($lg) {
      padding-right: 0;

      // margin-top: 15px;
    }
  }
}

.body-slide-img {
  height: 100%;
  background-size: contain !important;
}

.left,
.right {
  z-index: 1;
  position: relative;
  cursor: pointer;
  display: inline-block;
  width: 20px;
  height: 20px;
  background-size: contain !important;

  @include grid-media($sm) {
    width: 20px;
    height: 20px;
  }


  @include grid-media($lg) {
    width: 25px;
    height: 25px;
  }
}

.left {
  background: url("/wp-content/themes/seo-main/assets/img/icons/Back-Button-Active.svg") left center no-repeat;

  &:hover,
  &:focus,
  &.inactive {
    background: url("/wp-content/themes/seo-main/assets/img/icons/Back-Button-Inactive.svg") left center no-repeat;
  }
}

.right {
  background: url("/wp-content/themes/seo-main/assets/img/icons/Forward-Button-Active.svg") right center no-repeat;

  &:hover,
  &:focus,
  &.inactive {
    background: url("/wp-content/themes/seo-main/assets/img/icons/Forward-Button-Inactive.svg") right center no-repeat;
  }
}

.cslide {
  display: inline-block;
  text-align: center;
  margin-right: 5px;
}

.eslide {
  display: inline-block;
  text-align: center;
  margin-left: 5px;
}

.pagination,
.arrows {
  float: left;
}

.arrows {
  // margin-right: 25px;
  line-height: 0;
  text-align: right;
}

.pagination {
  @include type-5;
}

.active {
  display: block !important;
}

// POPUP
.po__popup {
  background: $white;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
  display: flex;
  flex-wrap: wrap;

  // transition: all 0.4s $cubic-transition;
  // transform: scale(0);
  // opacity: 0;

  @include grid-media($lg) {
    @include grid-collapse;
  }


  .po__content {
    padding: 30px;
    height: 75%;

    @include grid-media($md) {
      height: 65%;
    }


    @include grid-media($lg) {
      display: flex;
      align-items: center;
      height: 100%;
      padding: 0 30px;

      .po__content-wrapper {
        max-width: 550px;
      }

      @include grid-column(7);

      @include grid-shift(-5);
    }


    a {
      background-image: linear-gradient(to right, rgba(210, 42, 47, 1) 0%, rgba(255, 103, 27, 1) 100%);
      background-repeat: repeat-x;
      background-position: 0 100%;
      background-size: 100% 2px;
      padding-bottom: 5px;
    }
  }

  .po__content-title {
    margin: 0;
  }

  &.popup-active {
    transform: scale(1);
    opacity: 1;
    z-index: 11;
  }
}

.po__image {
  @include grid-column(12, $no-gutter);

  background-size: cover !important;
  height: 25%;

  @include grid-media($md) {
    height: 35%;
  }


  @include grid-media($lg) {
    @include grid-column(5);

    @include grid-shift(7);

    height: 100%;
  }
}

.po__close {
  position: absolute;
  top: 60px;
  right: 60px;
  width: 48px;
  height: 48px;
  z-index: 1;
  background: rgba(43, 43, 43, 0.75);
  border-radius: 100%;
  display: flex;
  justify-content: center;
  align-items: center;

  @include grid-media($lg) {
    background: $dark-gray-two;
    right: 60px;
  }


  img {
    width: 20px;
  }
}

.gal-row.slider.image-area, .gal-row.slider.control-area {
  @include grid-column(12, $no-gutter);

  border-left: 10px solid $black;
  padding-left: 10px;
}

.image-area {
  height: 148px;

  @include grid-media($lg) {
    height: 315px;
  }
}

.control-area {
  height: auto;
  min-height: 25px;

  @include grid-media($lg) {
    padding-top: 30px;
    min-height: 55px;
  }
}

.cslide, .eslide {
  @include type-custom($color: $black, $font-weight: $font-bold, $font-size: 45px, $line-height: 1);

  @include grid-media($lg) {
    @include type-custom($color: $black, $font-weight: $font-bold, $font-size: 61px, $line-height: 1);
  }
}

.eslide {
  font-weight: $font-thinnest;
}
